import React, { useState } from 'react'
import DisplaySingle from './DisplaySingle'

export default function DisplayData({userData}) {
    const [deleted, setDeleted] = useState("");
    const deleteHandler=(id)=>{
        setDeleted((id)=>{
            const deleteFilter = userData && userData.filter(data =>  data.id !== deleted);
            return deleteFilter
        })

    }

  return (
    <div className=' d-flex flex-column justify-content-center align-items-center mt-2 '>
        {deleteFilter &&deleteFilter.map((item)=>{
          return (<div style={{border:'2px solid grey'}}  className='text-center w-25 mt-2' >
           <DisplaySingle name={item.username} age={item.age} id={item.id} onDeleted={deleteHandler}/>
          </div>
          )
        })}
    </div>
  )
}
