import React from "react";
import reactDOM from 'react-dom'
import classes from './ErrorModaal.css';
import Card from "./Card";
const Backdrop =props=>{
  <div className={classes.backdrop} onClick={props.onComfirm}  />
}
const ModelOverlay=(props)=>{
  <Card className={classes.modal} >
  <header className="header">
    <h2>{props.title}</h2>
  </header>
  <div className="content">
    <p>{props.message}</p>
  </div>
  <footer className="actions">
    <button className="btn btn-primary">cancel</button>
  </footer>
</Card>
}
export default function ErrorModal(props) {
  return (
    <>
     {reactDOM.createPortal(<Backdrop onComfirm={props.onComfirm}/>,document.getElementById('backdrop-root'))}
     {reactDOM.createPortal(<ModelOverlay title={props.title} onComfirm={props.onComfirm}/>,document.getElementById('model-root'))}
    </>
  );
}
