import React, { useState } from "react";
import Card from "../UI/Card";
import ErrorModal from "../UI/ErrorModal";

export default function UserInput({ onSaveData }) {
  const [username, setusername] = useState("");
  const [error, setError] = useState();
  const [age, setAge] = useState("");
  const usernameChangeHandler = (e) => {
    setusername(e.target.value);
  };
  const ageChangeHandler = (e) => {
    setAge(e.target.value);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (username.trim().length === 0 || age.trim().length === 0) {
      setError({
        title: "you should'nt write an empty string",
        message: "you should'nt write an empty string",
      });
      return;
    }
    if (username.trim().length < 3 ) {
      setError({
        title: "you should enter at least 3 characters",
      });
      return;
    }

    if (age < 1) {
      setError({
        message: "you should enter a valid age (>0).",
      });
      return;
    }
    setError({ title: "", message: "" });
    setusername("");
    setAge("");
    const data = {
      username: username,
      age: age,
      id: Math.random().toString(),
    };
    onSaveData(data);
  };
  const errorHandler = () => {
    setError(null);
  };
  return (
    <div>
     { error && <ErrorModal title={error.usernamee} message={error.agee}  onConfirm={errorHandler}/>}
      <div className=" text-center mt-2 d-flex justify-content-center ">
        <form className="w-25" onSubmit={submitHandler}>
          <div className="mb-3 ">
            <label htmlFor="exampleInputEmail1" className="form-label">
              UserName
            </label>
            <input
              value={username}
              onChange={usernameChangeHandler}
              type="text"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
            {/* <p>{error.usernamee}</p> */}
            {error.title && (
              <p className="text-danger">{error.usernamee}</p>
            )}
          </div>
          <div className="mb-3">
            <label htmlFor="exampleInputage1" className="form-label">
              Age
            </label>
            <input
              value={age}
              onChange={ageChangeHandler}
              type="number"
              className="form-control"
              id="exampleInputage1"
            />
            {error.agee && <p className="text-danger">{error.agee}</p>}
          </div>
          <button type="submit" className="btn btn-primary">
            Add user
          </button>
        </form>
      </div>
    </div>
  );
}
